import React, { useState, useEffect } from 'react';
import './MarketTrendsTable.css';

const MarketTrendsTable = () => {
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMarketTrends = async () => {
      try {
        const response = await fetch('/api/market-trends'); // Уже правильный маршрут
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setAssets(data);
      } catch (err) {
        console.error('Error fetching market trends:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchMarketTrends();
  }, []);

  if (loading) {
    return (
      <div className="market-trends-section">
        <h2 className="market-trends-title"><span className="highlight">Market</span> Trend</h2>
        <div className="table-container">
          Loading market trends...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="market-trends-section">
        <h2 className="market-trends-title"><span className="highlight">Market</span> Trend</h2>
        <div className="table-container">
          Error: {error}
        </div>
      </div>
    );
  }

  return (
    <div className="market-trends-section">
      <h2 className="market-trends-title"><span className="highlight">Market</span> Trend</h2>
      <div className="table-container">
        <table className="trends-table">
          <thead>
            <tr>
              <th className="trend-th">#</th>
              <th className="trend-th">Name</th>
              <th className="trend-th">Price</th>
              <th className="trend-th">24h Change</th>
              <th className="trend-th">Action</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td colSpan="5" className="horizontal-divider"></td>
            </tr>
            {assets.length > 0 ? (
              assets.map((asset) => (
                <tr key={asset.id}>
                  <td className="trend-td">{asset.rank}</td>
                  <td className="trend-td">{asset.name}</td>
                  <td className="trend-td">${asset.price}</td>
                  <td className="trend-td">{asset.change_24h}</td>
                  <td className="trend-td action-cell">
                    <button className="action-btn">Trade</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="no-data">No data available</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default MarketTrendsTable;
