import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import Footer from './Footer';
import './OverviewPage.css';
import UserProfileSection from './UserProfileSection';
import OverviewSecuritySection from './OverviewSecuritySection';
import OverviewIdentificationSection from './OverviewIdentificationSection';
import OverviewPreferenceSection from './OverviewPreferenceSection';
import './OverviewPage.css';

const OverviewPage = () => {
  const [loginActivityData, setLoginActivityData] = useState([]);

  useEffect(() => {
    // Fetch login activity data from backend
    const fetchLoginActivity = async () => {
      try {
        const response = await fetch('/api/login-activity');
        const data = await response.json();
        setLoginActivityData(data);
      } catch (error) {
        console.error('Error fetching login activity:', error);
        // Fallback data in case of error
        setLoginActivityData([
          { id: 1, time: '2025-04-24 19:50:45', status: 'Success', ip: '192.72.134.201' },
          { id: 2, time: '2025-04-23 14:2:11', status: 'Fail', ip: '104.16.201.8' },
          { id: 3, time: '2025-04-22 09:15:33', status: 'Success', ip: '72.21.214.11' },
          { id: 4, time: '2025-04-21 22:40:57', status: 'Success', ip: '185.64.12.55' },
          { id: 5, time: '2025-04-20 06:05:02', status: 'Fail', ip: '45.91.184.62' }
        ]);
      }
    };

    fetchLoginActivity();
  }, []);

  return (
    <div className="overview-page">
      <Sidebar />
      <div className="overview-content">
        <Header />
        <div className="main-content">
          <UserProfileSection />
          <div className="overview-sections">
            <OverviewSecuritySection />
            <OverviewIdentificationSection />
            <OverviewPreferenceSection />
          </div>
          
          <div className="login-activity-section">
            <h2>Log In Activity</h2>
            <table className="login-activity-table">
              <thead>
                <tr>
                  <th>Time</th>
                  <th>Login Status</th>
                  <th>IP</th>
                </tr>
              </thead>
              <tbody>
                {loginActivityData.map((activity, index) => (
                  <tr key={index}>
                    <td>{activity.time}</td>
                    <td className={activity.status === 'Success' ? 'status-success' : 'status-fail'}>
                      {activity.status}
                    </td>
                    <td>{activity.ip}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default OverviewPage;
