import React, { useState, useEffect } from 'react';
import './MarketTrendsTable.css';

const MarketTrendsTable = () => {
  const [assets, setAssets] = useState([]);

  // Заглушка для получения данных из API
  useEffect(() => {
    // В реальной реализации здесь будет вызов API для получения данных из базы данных
    // fetch('/api/market-trends')
    //   .then(response => response.json())
    //   .then(data => setAssets(data))
    //   .catch(error => console.error('Error fetching market trends:', error));

    // Временная заглушка для демонстрации структуры
    setAssets([
      // Здесь будут данные из базы данных
    ]);
  }, []);

  return (
    <div className="market-trends-section">
      <h2 className="market-trends-title"><span className="highlight">Market</span> Trend</h2>
      <div className="table-container">
        <table className="trends-table">
          <thead>
            <tr>
              <th className="trend-th">#</th>
              <th className="trend-th">Name</th>
              <th className="trend-th">Price</th>
              <th className="trend-th">24h Change</th>
              <th className="trend-th">Action</th>
            </tr>
          </thead>
          <tbody>
            {assets.length > 0 ? (
              assets.map((asset, index) => (
                <tr key={asset.id || index}>
                  <td className="trend-td">{index + 1}</td>
                  <td className="trend-td">{asset.name}</td>
                  <td className="trend-td">{asset.price}</td>
                  <td className="trend-td">{asset.change24h}</td>
                  <td className="trend-td">
                    <button className="action-btn">Trade</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="no-data">No data available</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default MarketTrendsTable;
