import React, { useState } from 'react';
import './DashboardPage.css';

const DashboardPage = () => {
  const [walletExpanded, setWalletExpanded] = useState(false);

  const toggleWallet = () => {
    setWalletExpanded(!walletExpanded);
  };

  return (
    <div className="dashboard-page">
      <div className="sidebar">
        <div className="sidebar-header">
          <img src="/image/sidebar/home.svg" alt="Home" className="sidebar-icon" />
          <span className="sidebar-title">Dashboard</span>
        </div>
        <div className="sidebar-menu">
          <div className="sidebar-item" onClick={toggleWallet}>
            <img src="/image/sidebar/wallet.svg" alt="Wallet" className="sidebar-icon" />
            <span className="sidebar-text">Wallet</span>
            <img 
              src={`/image/sidebar/${walletExpanded ? 'arrow_up' : 'arrow_down'}.svg`} 
              alt="Toggle" 
              className="sidebar-arrow" 
            />
          </div>
          {walletExpanded && (
            <div className="submenu">
              <div className="submenu-item">Asset</div>
              <div className="submenu-item">Deposit</div>
              <div className="submenu-item">Withdraw</div>
            </div>
          )}
        </div>
      </div>
      <div className="dashboard-content">
        <h1>Dashboard</h1>
        <p>Welcome to your dashboard. This is where your trading activity and account information will be displayed.</p>
      </div>
    </div>
  );
}

export default DashboardPage;
